sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var caller = current.getValue("caller_id");
    var caller_name = current.getDisplayValue("caller_id");
    var opened_by = current.opened_by;
    var number = current.number;
	var botServiceAccount = "";

	var itsm_horiz = new GlideRecord("x_831378_kore_ai_0_it_assist_config_horiz");
	itsm_horiz.query();
	var rm = new sn_ws.RESTMessageV2("ITAssistWorkbenchPing", "ITAssistWorkbenchPing");
	var workbenchheaders = [
		"accept", "authorization", "datatable", "endpoint",
		"integration_service", "platform", "serv_host"
	];
	while (itsm_horiz.next()) {
		for (var header in workbenchheaders) {
			var header_val = itsm_horiz[workbenchheaders[header]] + '';
			if (workbenchheaders[header] == "authorization")
				header_val = itsm_horiz.getElement("authorization").getDecryptedValue();
			rm.setStringParameterNoEscape(workbenchheaders[header], header_val);
		}
		botServiceAccount = itsm_horiz["bot_service_account"].toString();
	}

    if (opened_by.toString() !== botServiceAccount) {

        var actions = [];
        actions.push({
            "type": "postback",
            "label": "View ticket " + number,
            "value": "View ticket " + number
        });


        var notification_text = "\nYour IT ticket " + number + " just landed in ServiceNow! You can check with me for any updates. Also, if you need any other help, just ask me.";
        var item = {
            "_id": "_id",
            "body": notification_text,
            "target_id": caller,
            "target_type": "USER",
            "actions": actions,
        };
        rm.setRequestBody(JSON.stringify({
            "notifications": [item]
        }));
        var response = rm.executeAsync();
    }
})(current, previous);
